import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from "./components/Navbar"
import './App.css';

// Set up axios defaults
axios.defaults.baseURL = 'http://localhost:5000';

// Auth Context
const AuthContext = React.createContext();

// Components


const Login = ({ onLogin }) => {
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'buyer',
    company: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint = isRegister ? '/api/register' : '/api/login';
      const response = await axios.post(endpoint, formData);

      if (isRegister) {
        setError('');
        alert('Registration successful! Please login.');
        setIsRegister(false);
      } else {
        localStorage.setItem('token', response.data.token);
        axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.token}`;
        onLogin(response.data.user);
      }
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>{isRegister ? 'Register' : 'Login'}</h2>
        {error && <div className="error">{error}</div>}
        <form onSubmit={handleSubmit}>
          {isRegister && (
            <input
              type="text"
              placeholder="Full Name"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              required
            />
          )}
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
          />
          {isRegister && (
            <>
              <select
                value={formData.role}
                onChange={(e) => setFormData({...formData, role: e.target.value})}
              >
                <option value="buyer">Buyer</option>
                <option value="vendor">Vendor</option>
                <option value="approver">Approver</option>
              </select>
              <input
                type="text"
                placeholder="Company Name"
                value={formData.company}
                onChange={(e) => setFormData({...formData, company: e.target.value})}
                required
              />
            </>
          )}
          <button type="submit" className="btn btn-primary">
            {isRegister ? 'Register' : 'Login'}
          </button>
        </form>
        <p>
          {isRegister ? 'Already have an account?' : "Don't have an account?"}
          <button
            type="button"
            className="link-btn"
            onClick={() => setIsRegister(!isRegister)}
          >
            {isRegister ? 'Login' : 'Register'}
          </button>
        </p>
      </div>
    </div>
  );
};

const Dashboard = ({ user }) => {
  const [stats, setStats] = useState({});

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/dashboard/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      <div className="stats-grid">
        {user.role === 'buyer' && (
          <>
            <div className="stat-card">
              <h3>Total RFPs</h3>
              <p className="stat-number">{stats.totalRFPs || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Active RFPs</h3>
              <p className="stat-number">{stats.activeRFPs || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Total Orders</h3>
              <p className="stat-number">{stats.totalOrders || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Pending Orders</h3>
              <p className="stat-number">{stats.pendingOrders || 0}</p>
            </div>
          </>
        )}
        {user.role === 'vendor' && (
          <>
            <div className="stat-card">
              <h3>Total Quotes</h3>
              <p className="stat-number">{stats.totalQuotes || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Accepted Quotes</h3>
              <p className="stat-number">{stats.acceptedQuotes || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Total Orders</h3>
              <p className="stat-number">{stats.totalOrders || 0}</p>
            </div>
            <div className="stat-card">
              <h3>Active Orders</h3>
              <p className="stat-number">{stats.activeOrders || 0}</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

const RFPList = ({ user }) => {
  const [rfps, setRfps] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchRFPs();
  }, []);

  const fetchRFPs = async () => {
    try {
      const response = await axios.get('/api/rfps');
      setRfps(response.data);
    } catch (error) {
      console.error('Error fetching RFPs:', error);
    }
  };

  const publishRFP = async (id) => {
    try {
      await axios.put(`/api/rfps/${id}/publish`);
      fetchRFPs();
    } catch (error) {
      console.error('Error publishing RFP:', error);
    }
  };

  return (
    <div className="rfp-list">
      <div className="section-header">
        <h2>RFPs</h2>
        {user.role === 'buyer' && (
          <button
            onClick={() => setShowForm(true)}
            className="btn btn-primary"
          >
            Create RFP
          </button>
        )}
      </div>

      {showForm && (
        <RFPForm
          onClose={() => setShowForm(false)}
          onSubmit={() => {
            setShowForm(false);
            fetchRFPs();
          }}
        />
      )}

      <div className="rfp-grid">
        {rfps.map((rfp) => (
          <div key={rfp._id} className="rfp-card">
            <h3>{rfp.title}</h3>
            <p className="rfp-category">{rfp.category}</p>
            <p className="rfp-budget">Budget: ${rfp.budget.toLocaleString()}</p>
            <p className="rfp-deadline">Deadline: {new Date(rfp.deadline).toLocaleDateString()}</p>
            <p className="rfp-status">{rfp.status}</p>

            <div className="rfp-actions">
              {user.role === 'buyer' && rfp.status === 'draft' && (
                <button
                  onClick={() => publishRFP(rfp._id)}
                  className="btn btn-success"
                >
                  Publish
                </button>
              )}
              {user.role === 'vendor' && rfp.status === 'published' && (
                <button className="btn btn-primary">
                  Submit Quote
                </button>
              )}
              <button className="btn btn-secondary">View Details</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const RFPForm = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    budget: '',
    deadline: '',
    requirements: ['']
  });

  const addRequirement = () => {
    setFormData({
      ...formData,
      requirements: [...formData.requirements, '']
    });
  };

  const updateRequirement = (index, value) => {
    const newRequirements = [...formData.requirements];
    newRequirements[index] = value;
    setFormData({ ...formData, requirements: newRequirements });
  };

  const removeRequirement = (index) => {
    const newRequirements = formData.requirements.filter((_, i) => i !== index);
    setFormData({ ...formData, requirements: newRequirements });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataObj = new FormData();
      Object.keys(formData).forEach(key => {
        if (key === 'requirements') {
          formDataObj.append(key, JSON.stringify(formData[key].filter(req => req.trim())));
        } else {
          formDataObj.append(key, formData[key]);
        }
      });

      await axios.post('/api/rfps', formDataObj, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      onSubmit();
    } catch (error) {
      console.error('Error creating RFP:', error);
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h3>Create New RFP</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="RFP Title"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
          />

          <textarea
            placeholder="Description"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            required
          />

          <select
            value={formData.category}
            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
            required
          >
            <option value="">Select Category</option>
            <option value="Office Supplies">Office Supplies</option>
            <option value="IT Hardware">IT Hardware</option>
            <option value="Raw Materials">Raw Materials</option>
            <option value="Services">Services</option>
          </select>

          <input
            type="number"
            placeholder="Budget"
            value={formData.budget}
            onChange={(e) => setFormData({ ...formData, budget: e.target.value })}
            required
          />

          <input
            type="date"
            value={formData.deadline}
            onChange={(e) => setFormData({ ...formData, deadline: e.target.value })}
            required
          />

          <div className="requirements-section">
            <h4>Requirements</h4>
            {formData.requirements.map((req, index) => (
              <div key={index} className="requirement-row">
                <input
                  type="text"
                  placeholder={`Requirement ${index + 1}`}
                  value={req}
                  onChange={(e) => updateRequirement(index, e.target.value)}
                />
                {formData.requirements.length > 1 && (
                  <button
                    type="button"
                    onClick={() => removeRequirement(index)}
                    className="btn btn-danger"
                  >
                    Remove
                  </button>
                )}
              </div>
            ))}
            <button
              type="button"
              onClick={addRequirement}
              className="btn btn-secondary"
            >
              Add Requirement
            </button>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn btn-primary">
              Create RFP
            </button>
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

