import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AIVendorEvaluation = ({ quotes, rfp, onClose }) => {
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (quotes && quotes.length > 0) {
      analyzeQuotes();
    }
  }, [quotes]);

  const analyzeQuotes = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('http://localhost:5001/api/ai/analyze-quotes', {
        quotes: quotes,
        rfp: rfp
      });

      if (response.data.success) {
        setAnalysis(response.data.analysis);
      } else {
        throw new Error('Analysis failed');
      }
    } catch (error) {
      console.error('AI Analysis Error:', error);
      setError('AI analysis service unavailable. Showing basic analysis.');
      // Fallback to basic analysis
      performBasicAnalysis();
    } finally {
      setLoading(false);
    }
  };

  const performBasicAnalysis = () => {
    // Basic client-side analysis as fallback
    const analyzedQuotes = quotes.map((quote, index) => {
      const priceScore = ((Math.max(...quotes.map(q => q.totalPrice)) - quote.totalPrice) / 
                          Math.max(...quotes.map(q => q.totalPrice))) * 100;
      const deliveryScore = ((Math.max(...quotes.map(q => q.deliveryTime)) - quote.deliveryTime) / 
                            Math.max(...quotes.map(q => q.deliveryTime))) * 100;
      const overallScore = (priceScore * 0.5 + deliveryScore * 0.3 + Math.random() * 20 + 70);

      return {
        ...quote,
        ai_analysis: {
          overall_score: Math.round(overallScore),
          cost_score: Math.round(priceScore),
          delivery_score: Math.round(deliveryScore),
          compliance_score: 85,
          price_competitiveness: Math.round(priceScore),
          vendor_rating: Math.round(Math.random() * 30 + 70),
          risk_level: overallScore > 80 ? 'Low' : overallScore > 60 ? 'Medium' : 'High',
          recommendation: overallScore > 85 ? 'Highly Recommended' : 
                         overallScore > 70 ? 'Recommended' : 
                         overallScore > 55 ? 'Consider with Caution' : 'Not Recommended',
          insights: [
            `Price competitiveness: ${Math.round(priceScore)}%`,
            `Delivery performance: ${Math.round(deliveryScore)}%`,
            'Analysis based on basic comparison'
          ],
          confidence: 'Medium'
        }
      };
    });

    setAnalysis({
      analyzed_quotes: analyzedQuotes.sort((a, b) => b.ai_analysis.overall_score - a.ai_analysis.overall_score),
      summary: {
        total_quotes: quotes.length,
        average_score: Math.round(analyzedQuotes.reduce((sum, q) => sum + q.ai_analysis.overall_score, 0) / quotes.length),
        best_vendor: analyzedQuotes[0]?.vendorId?.name || 'Unknown'
      },
      recommendations: [
        {
          type: 'top_choice',
          title: 'Recommended Vendor',
          description: 'Based on price and delivery analysis',
          action: 'Consider for contract award'
        }
      ]
    });
  };

  if (loading) {
    return (
      <div className="modal">
        <div className="modal-content ai-analysis">
          <div className="ai-loading">
            <div className="loading-spinner"></div>
            <h3>ü§ñ AI Analysis in Progress</h3>
            <p>Analyzing vendor quotes using machine learning models...</p>
            <div className="loading-steps">
              <div className="step">‚úì Processing quote data</div>
              <div className="step">‚è≥ Evaluating vendor performance</div>
              <div className="step">‚è≥ Calculating risk assessments</div>
              <div className="step">‚è≥ Generating recommendations</div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!analysis) {
    return (
      <div className="modal">
        <div className="modal-content">
          <div className="error">No analysis available</div>
          <button onClick={onClose} className="btn btn-secondary">Close</button>
        </div>
      </div>
    );
  }

  return (
    <div className="modal">
      <div className="modal-content ai-analysis">
        <div className="modal-header">
          <h3>ü§ñ AI Vendor Analysis Results</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>

        {error && (
          <div className="ai-warning">
            <p>‚ö†Ô∏è {error}</p>
          </div>
        )}

        <div className="ai-summary">
          <div className="summary-stats">
            <div className="stat-item">
              <label>Total Quotes Analyzed</label>
              <span>{analysis.summary.total_quotes}</span>
            </div>
            <div className="stat-item">
              <label>Average AI Score</label>
              <span>{analysis.summary.average_score}</span>
            </div>
            <div className="stat-item">
              <label>Recommended Vendor</label>
              <span>{analysis.summary.best_vendor}</span>
            </div>
          </div>
        </div>

        <div className="ai-quotes">
          <h4>üìä Quote Analysis Results</h4>
          {analysis.analyzed_quotes.map((quote, index) => (
            <div key={quote._id} className={`ai-quote-card ${index === 0 ? 'top-choice' : ''}`}>
              <div className="quote-header">
                <div className="vendor-info">
                  <h5>{quote.vendorId.name}</h5>
                  <span className="vendor-company">{quote.vendorId.company}</span>
                  {index === 0 && <span className="top-choice-badge">üèÜ AI Recommended</span>}
                </div>
                <div className="overall-score">
                  <div className={`score-circle ${getScoreColor(quote.ai_analysis.overall_score)}`}>
                    {quote.ai_analysis.overall_score}
                  </div>
                  <span className="score-label">AI Score</span>
                </div>
              </div>

              <div className="ai-metrics">
                <div className="metric">
                  <label>Cost Score</label>
                  <div className="metric-bar">
                    <div 
                      className="metric-fill" 
                      style={{width: `${quote.ai_analysis.cost_score}%`}}
                    ></div>
                    <span>{quote.ai_analysis.cost_score}%</span>
                  </div>
                </div>
                <div className="metric">
                  <label>Delivery Score</label>
                  <div className="metric-bar">
                    <div 
                      className="metric-fill" 
                      style={{width: `${quote.ai_analysis.delivery_score}%`}}
                    ></div>
                    <span>{quote.ai_analysis.delivery_score}%</span>
                  </div>
                </div>
                <div className="metric">
                  <label>Compliance Score</label>
                  <div className="metric-bar">
                    <div 
                      className="metric-fill" 
                      style={{width: `${quote.ai_analysis.compliance_score}%`}}
                    ></div>
                    <span>{quote.ai_analysis.compliance_score}%</span>
                  </div>
                </div>
              </div>

              <div className="ai-insights">
                <div className="recommendation">
                  <span className={`rec-badge ${quote.ai_analysis.recommendation.toLowerCase().replace(/\s+/g, '-')}`}>
                    {quote.ai_analysis.recommendation}
                  </span>
                  <span className={`risk-level ${quote.ai_analysis.risk_level.toLowerCase()}`}>
                    Risk: {quote.ai_analysis.risk_level}
                  </span>
                </div>
                <div className="insights-list">
                  {quote.ai_analysis.insights.map((insight, idx) => (
                    <div key={idx} className="insight-item">
                      {insight}
                    </div>
                  ))}
                </div>
              </div>

              <div className="quote-basic-info">
                <div className="info-row">
                  <span>Total Price: ${quote.totalPrice.toLocaleString()}</span>
                  <span>Delivery: {quote.deliveryTime} days</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="ai-recommendations">
          <h4>üéØ Strategic Recommendations</h4>
          {analysis.recommendations.map((rec, index) => (
            <div key={index} className={`recommendation-card ${rec.type}`}>
              <div className="rec-header">
                <h5>{rec.title}</h5>
              </div>
              <p>{rec.description}</p>
              <div className="rec-action">
                <strong>Action:</strong> {rec.action}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  function getScoreColor(score) {
    if (score >= 85) return 'excellent';
    if (score >= 70) return 'good';
    if (score >= 55) return 'fair';
    return 'poor';
  }
};

export default AIVendorEvaluation;
